<?phpif ( ! defined('BASEPATH')) exit('No direct script access allowed');class Spm_gred_year extends CI_Model {		function __construct()		{			parent::__construct();		}#############################################################################################################################//CRUD for user_data	//SELECT	//select			function get($id = FALSE){		if ($id === FALSE)        {            return $this->db->get('spm_gred_year');        }        return $this->db->get_where('spm_gred_year', array('id'=>$id));  	}		function get_where($array){		return $this->db->get_where('spm_gred_year', $array);	}		function get_custom_where($where){		return $this->db->where($where)->get('spm_gred_year');	}}